// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IRouter} from "onlyswaps-solidity/src/interfaces/IRouter.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";


contract CrossChainSwapper {
    // The only swaps Router - this is the main contract we interact with
    IRouter public immutable router;

    // Events for tracking swaps
    event SwapInitiated(
        bytes32 indexed requestId,
        address indexed initiator,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 solverFee,
        uint256 destinationChainId,
        address recipient
    );

    event SwapParametersQueried(
        bytes32 indexed requestId,
        bool executed,
        uint256 requestedAt
    );


    constructor(address _routerAddress) {
        require(_routerAddress != address(0), "Invalid router address");
        router = IRouter(_routerAddress);
    }

    // We'll add functions here next
        function initiateSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 solverFee,
        uint256 destinationChainId,
        address recipient
    ) external returns (bytes32 requestId) {
        // Validation
        require(amountIn > 0, "Amount must be greater than zero");
        require(solverFee > 0, "Solver fee must be greater than zero");
        require(recipient != address(0), "Invalid recipient address");
        require(destinationChainId != block.chainid, "Cannot swap to same chain");

        // Calculate total amount needed: swap amount + solver fee
        uint256 totalAmount = amountIn + solverFee;

        // Transfer tokens from the caller to this contract
        // The caller must have approved this contract first
        require(
            IERC20(tokenIn).transferFrom(msg.sender, address(this), totalAmount),
            "Transfer failed - check allowance"
        );

        // Approve the Router to spend our tokens
        require(
            IERC20(tokenIn).approve(address(router), totalAmount),
            "Approval failed"
        );

        // Call the Router to request the cross-chain swap
        requestId = router.requestCrossChainSwap(
            tokenIn,
            tokenOut,
            amountIn,
            amountOut,
            solverFee,
            destinationChainId,
            recipient
        );

        // Emit event for tracking
        emit SwapInitiated(
            requestId,
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            amountOut,
            solverFee,
            destinationChainId,
            recipient
        );

        return requestId;
    }
}

